etCurrentDialog = function () { if (aria.OpenDialogList &amp;&amp; aria.OpenDialogList.length) { return aria.OpenDialogList[aria.OpenDialogList.length - 1]; } }; aria.closeCurrentDialog = function () { var currentDialog = aria.getCurrentDialog(); if (currentDialog) { currentDialog.close(); return true; } return false; }; aria.handleEscape = function (event) { var key = event.which || event.keyCode; if (key === aria.KeyCode.ESC &amp;&amp; aria.closeCurrentDialog()) { event.stopPropagation(); } }; document.addEventListener(&#39;keyup&#39;, aria.handleEscape); /** * @class * @description Dialog object providing modal focus management. * * Assumptions: The element serving as the dialog container is present in the * DOM and hidden. The dialog container has role=&#39;dialog&#39;. * * @param dialogId * The ID of the element serving as the dialog container. * @param focusAfterClosed * Either the DOM node or the ID of the DOM node to focus when the * dialog closes. * @param focusFirst * Optional parameter containing either the DOM node or the ID of the * DOM node to focus when the dialog opens. If not specified, the * first focusable element in the dialog will receive focus. */ aria.Dialog = function (dialogId, focusAfterClosed, focusFirst) { this.dialogNode = document.getElementById(dialogId); if (this.dialogNode === null) { throw new Error(&#39;No element found with id=&#34;&#39; + dialogId + &#39;&#34;.&#39;); } var validRoles = [&#39;dialog&#39;, &#39;alertdialog&#39;]; var isDialog = (this.dialogNode.getAttribute(&#39;role&#39;) || &#39;&#39;) .trim() .split(/\s+/g) .some(function (token) { return validRoles.some(function (role) { return token === role; }); }); if (!isDialog) { throw new Error( &#39;Dialog() requires a DOM element with ARIA role of dialog or alertdialog.&#39; ); } // Wrap in an individual backdrop element if one doesn&#39;t exist // Native &lt;dialog&gt; elements use the ::backdrop pseudo-element, which // works similarly. var backdropClass = &#39;dialo