/** Modale **/ :root { --BORDER-color: #b0aeae; --BACKGROUND-color: #FFFFFF; --BODY-FADE-color: rgba(0, 0, 0, 0.5); --BOX-SHADOW: 0 19px 38px rgba(0, 0, 0, 0.12), 0 15px 12px rgba(0, 0, 0, 0.22); --FOCUS-color: #252d91; } .none { display: none; } .openModale:focus, .closeModale:focus { outline: 3px solid var(--FOCUS-color); } [role=&#34;alertdialog&#34;], [role=&#34;dialog&#34;] { box-sizing: border-box; padding: 1em; border: 1px solid var(--BORDER-color); background-color: var(--BACKGROUND-color); min-height: 100vh; z-index: 1030; } @media screen and (min-width: 640px) { [role=&#34;alertdialog&#34;], [role=&#34;dialog&#34;] { position: absolute; top: 6rem; left: 50vw; /* move to the middle of the screen (assumes relative parent is the body/viewport) */ transform: translateX(-50%); /* move backwards 50% of this element&#39;s width */ min-width: calc(640px - (15px * 2)); /* == breakpoint - left+right margin */ min-height: auto; box-shadow: var(-BOX-SHADOW); } } .dialog-header { border-bottom: 1px solid var(--BORDER-color); padding-bottom: 1.5em; } .dialog-header h1 { font-size: 1.5em; margin: 0; } .dialog-header button { float: right; position: absolute; top: 1.2em; right: 2%; padding: 0.5em; } .dialog-body { padding: 1.5em 0.5em; } .dialog-footer button { float: right; padding: 0.5em; } /* native &lt;dialog&gt; element uses the ::backdrop pseudo-element */ /* dialog::backdrop, */ .dialog-backdrop { display: none; position: fixed; overflow-y: auto; top: 0; right: 0; bottom: 0; left: 0; } @media screen and (min-width: 640px) { .dialog-backdrop { background: var(--BODY-FADE-color); } } .dialog-backdrop.active { display: block; } .no-scroll { overflow-y: auto !important; } /* this is added to the body when a dialog is open */ .has-dialog { overflow: hidden; } Code javascript /* * This content is licensed according to the W3C Software License at * https://www.w3.org/Consortium/Legal/2015/copyright-software-and-document */ &#39;use strict&#39;; var aria = aria || {}; aria